import React, { useState, useRef, useEffect } from "react";
import NewTournament from "../../../components/Cards/NewTournament/NewTournament";
import ModalTournament from "../../../components/Modal/ModalTournament";
import {
  MenuFoldOutlined,
  MenuUnfoldOutlined,
  UserOutlined,
  TrophyOutlined,
  HomeOutlined,
  SmileOutlined,
  ImportOutlined,
} from "@ant-design/icons";
import { Layout, Menu } from "antd";
import "../../Layout/Layout.css";
import { Link, useParams } from "react-router-dom";
import "./OpenTournament.css";
import OpenTournamentTable from "./Table/OpenTournamentTable";
import api from "../../../services/api";
import ModalPairs from "../../../components/Modal/ModalPairs";
import AddPairs from "../../../components/Cards/AddPairs/AddPairs";

const OpenTournament = () => {
  const { pageID } = useParams();
  const [tournaments, setTournaments] = useState([]);

  const { Header, Sider, Content } = Layout;
  const [collapsed, setCollapsed] = useState(false);
  const [ModalPairsVisible, setModalPairsVisible] = useState(false);

  const getTournaments = async () => {
    const response = await api.get("/tournaments/" + pageID);

    const data = response.data;

    console.log(data);
    setTournaments(data);
  };

  useEffect(() => {
    getTournaments();
  }, []);

  return (
    <Layout>
      <Sider trigger={null} collapsible collapsed={collapsed}>
        <div className="user__information">
          <SmileOutlined />
        </div>
        <Menu
          className="antd-menu"
          theme="dark"
          mode="inline"
          defaultSelectedKeys={["3"]}
        >
          <Menu.Item key="1">
            <HomeOutlined />
            <span>Home</span>
            <Link to="/home"></Link>
          </Menu.Item>
          <Menu.Item key="2">
            <UserOutlined />
            <span>Usuários</span>
            <Link to="/users"></Link>
          </Menu.Item>
          <Menu.Item key="3">
            <TrophyOutlined />
            <span>Torneios</span>
            <Link to="/tournaments"></Link>
          </Menu.Item>
          <Menu.Item key="0" className="logout">
            <ImportOutlined />
            <span>Sair</span>
            <Link to="/"></Link>
          </Menu.Item>
        </Menu>
      </Sider>
      <Layout className="site-layout">
        <Header
          className="site-layout-header-background"
          style={{
            padding: 0,
          }}
        >
          {React.createElement(
            collapsed ? MenuUnfoldOutlined : MenuFoldOutlined,
            {
              className: "trigger",
              onClick: () => setCollapsed(!collapsed),
            }
          )}
          <div className="header__container">
            <h1 className="header__info">Padel Match</h1>
          </div>
        </Header>
        <Content
          className="site-layout-background"
          style={{
            margin: "24px 16px",
            padding: 24,
            minHeight: 280,
          }}
        >
          <div className="page-tournament__container">
            <div className="page-tournament__header">
              <div className="title_area">
                {tournaments.map((tournament) => (
                  <h1 className="page-tournament__title">{tournament.name}</h1>
                ))}
              </div>
              <a onClick={() => setModalPairsVisible(true)}>
                <AddPairs></AddPairs>
              </a>
            </div>
            {ModalPairsVisible ? (
              <ModalPairs onClose={() => setModalPairsVisible(false)} />
            ) : null}
            <OpenTournamentTable />
          </div>
        </Content>
      </Layout>
    </Layout>
  );
};

export default OpenTournament;
